import classNames from 'classnames';
import React from 'react';

function Popup({ isDarkBackground, children }) {
  const popupContentWrapperStyle = classNames(
    'w-[88%] h-fit flex items-center justify-center py-10 px-5 bg-white absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 rounded-larger shadow-xl',
    {
      'border-[5px] border-white border-solid bg-metallic-blue':
        isDarkBackground,
    }
  );

  return (
    <div
      data-testid="popup-container"
      className="backdrop-blur-xl flex flex-col justify-end absolute inset-0 z-50 h-full pc-w-full"
    >
      <div
        data-testid="popup-content-wrapper"
        className={popupContentWrapperStyle}
      >
        {children}
      </div>
    </div>
  );
}

Popup.defaultProps = {
  isDarkBackground: false,
};

export default Popup
