import React from "react";

export function TransactionPill({ description, amount, isIncome }) {
  const renderStyle = isIncome ? (
    <p className="w-4 h-4 bg-green rounded-2xl"></p>
  ) : (
    <p className="w-4 h-4 bg-red rounded-2xl"></p>
  );
  return (
    <div className="flex flex-row justify-between w-full h-[5vh] bg-background rounded-md px-2 items-center">
      <p>{description}</p>
      <p className="text-xl font-semibold">${amount}</p>
      {renderStyle}
    </div>
  );
}
