import classNames from "classnames";
import React from "react";

export function TransactionPill({ description, amount, isIncome }) {
  const containerStyle = classNames(
    "flex flex-row justify-between w-full h-[5vh] bg-background rounded-md px-2 items-center",
    {
      "border-r-1 border-primary": isIncome,
      "border-r-1 border-secondary": !isIncome,
    },
  );
  return (
    <div className="">
      <p>{description}</p>
      <p className="text-xl font-semibold">${amount}</p>
    </div>
  );
}
