import classNames from "classnames";
import React from "react";

export function TransactionPill({ description, amount }) {
  const containerStyle = classNames(
    "flex flex-row justify-between w-full h-[5vh] bg-background rounded-md px-2 items-center",
    {isIncome:'border-r'}
  );
  return (
    <div className="">
      <p>{description}</p>
      <p className="text-xl font-semibold">${amount}</p>
    </div>
  );
}
