import React, { useState } from "react";

export function OverView() {
  const [amount, setAmount] = useState("");
  const [description, setDescription] = useState("");

  const handleDescription = (event) => {
    const currentAmount = event.target.value;
    setAmount(currentAmount);
  };

  console.log(amount);
  return (
    <div className="flex items-center flex-col">
      <span className="font-semibold text-xl text-primary">
        Expense Tracker
      </span>
      <div className=" flex flex-col items-center bg-secondary h-[50vh] w-[70vw] rounded-md shadow-lg mt-4 pt-4 px-4 gap-4">
        <div clasflex flex-col items-center>
          <input
            type="number"
            className="border-none outline-none w-[85%] h-[4vh] bg-input shadow-md text-text placeholder:text-text px-2 rounded-xl placeholder:font-black"
            placeholder="Amount"
            value={amount}
            onChange={handleDescription}
          />
          <input
            type="text"
            className="border-none outline-none w-[85%] h-[4vh] bg-input shadow-md text-text placeholder:text-text placeholder:font-black px-2 rounded-xl"
            placeholder="Description"
            id="description"
          />
          <button
            type="button"
            className="bg-button px-5 py-2 rounded-[25px] shadow-md text-sm font-bold w-[70%] text-buttonText"
            style={{ WebkitTapHighlightColor: "transparent" }}
          >
            Add Transaction
          </button>
        </div>
      </div>

      <div>
        <p>Transaction history</p>
      </div>
    </div>
  );
}
