import React, { useState } from "react";
import { TransactionPill } from "./TransactionPill";

export function OverView() {
  const [amount, setAmount] = useState("");
  const [description, setDescription] = useState("");
  const [isShowTransaction, setIsShowTransaction] = useState(false);
  const [transaction, setTransaction] = useState([] || null);
  const [showErrorMessage, setShowErrorMessage] = useState(false);

  const options = [
    "select type",
    "food",
    "travel",
    "income",
    "medical",
    "dept",
  ];

  const handleDescription = (event) => {
    const currentDescription = event.target.value;
    setDescription(currentDescription);
  };

  const handleAmount = (event) => {
    const currentAmount = event.target.value;
    setAmount(currentAmount);
  };

  const handleAdd = () => {
    if (description && amount !== "") {
      const updatedTransaction = [...transaction, { amount, description }];

      setTransaction(updatedTransaction);
      setIsShowTransaction(true);
      setShowErrorMessage(false);
    } else {
      setShowErrorMessage(true);
    }
  };

  const handleClear = () => {
    setTransaction([]);
  };

  const totalPrice = transaction.reduce((cost, curr) => {
    if (curr.description === "income") {
      return Number(cost) + Number(curr.amount);
    }
    return cost;
  }, "");

  const totalExpense = transaction.reduce((cost, curr) => {
    if (curr.description !== "income") {
      return Number(cost) + Number(curr.amount);
    }
    return cost;
  }, "");

  const balance = totalPrice - totalExpense;

  // const totalIncome = transaction.reduce(());

  return (
    <div className="flex items-center flex-col gap-2">
      <span className="font-semibold text-xl text-primary">
        Expense Tracker
      </span>
      <div className=" flex flex-col items-center bg-secondary h-[70vh] w-[75vw] rounded-t-md shadow-lg mt-4 pt-4 px-4 gap-10 pb-8 min-h-[18vh]">
        <div className="flex flex-col items-center gap-3 relative">
          <input
            type="number"
            className="border-none outline-none w-full h-[4vh] bg-background shadow-md text-text placeholder:text-text px-2 rounded-sm"
            placeholder="Amount"
            value={amount}
            onChange={handleAmount}
          />
          <select
            onChange={handleDescription}
            value={description}
            className="w-full h-[4vh] bg-background outline-none rounded-sm"
          >
            {options.map((option) => (
              <option
                value={option}
                className="border-none rounded-xl outline-none"
              >
                {option}
              </option>
            ))}
          </select>
          <button
            type="button"
            className="bg-background px-5 py-2 rounded-[25px] shadow-md text-sm font-bold w-[70%] text-primary"
            style={{ WebkitTapHighlightColor: "transparent" }}
            onClick={handleAdd}
          >
            Add Transaction
          </button>
          {showErrorMessage && (
            <p className="text-red absolute inset-0 top-36 text-center text-sm">
              *Please fill blanks
            </p>
          )}
        </div>
        <p className=" underline underline-offset-4 mb-[-16px]">
          Transaction history
        </p>
        <div className="flex flex-col text-center w-full min-h-[32vh] overflow-y-scroll px-2">
          <div className="flex flex-col gap-3">
            {isShowTransaction &&
              transaction.map((item) => {
                const isIncome = item.description === "income";
                return (
                  <TransactionPill
                    description={item.description}
                    amount={item.amount}
                    key={item.amount}
                    isIncome={isIncome}
                  />
                );
              })}
          </div>
        </div>
        <button
          type="button"
          className="bg-background px-5 py-2 rounded-[25px] shadow-md text-sm font-bold w-[70%] text-primary"
          style={{ WebkitTapHighlightColor: "transparent" }}
          onClick={handleClear}
        >
          Clear Transaction
        </button>
      </div>
      <div className=" bg-secondary h-[19vh] w-[75vw] flex flex-col px-4 gap-2 rounded-b-md items-center pt-4">
        <div className="w-full bg-green h-[4vh] shadow-md rounded-sm flex justify-between flex-row pt-2 px-4 items-center">
          <p>Total Income</p>
          <p className="text-base font-semibold">- ₹{totalPrice}</p>
        </div>
        <div className="w-full bg-red h-[4vh] shadow-md rounded-sm flex justify-between flex-row px-4 items-center">
          <p>Total Expense</p>
          <p className="text-base font-semibold">- ₹{totalExpense}</p>
        </div>
        <div className="w-[20vh] bg-green h-[5vh] shadow-md flex justify-between flex-row px-4 items-center rounded-md">
          <p className="text-lg font-medium">Balance</p>
          <p className="text-lg font-semibold">- ₹{balance}</p>
        </div>
      </div>
    </div>
  );
}
