import React from "react";

export function TransactionPill({ description, amount, isIncome, date }) {
  const renderStyle = isIncome ? (
    <p className="w-4 h-4 bg-green rounded-2xl"></p>
  ) : (
    <p className="w-4 h-4 bg-red rounded-2xl"></p>
  );
  return (
    <div className="animate-top flex flex-row justify-between w-full h-[5vh] bg-background rounded-md px-2 items-center shadow-xl">
      <p className="text-lg font-semibold">{description}</p>
      <p className="text-base">{date}</p>
      <div className="flex flex-row gap-2 items-center">
        <p className="text-lg font-medium">â‚¹{amount}</p>
        {renderStyle}
      </div>
    </div>
  );
}
