import React, { useState } from "react";
import { TransactionPill } from "./TransactionPill";

export function OverView() {
  const [amount, setAmount] = useState("");
  const [description, setDescription] = useState("");
  const [isShowTransaction, setIsShowTransaction] = useState(false);
  const [transaction,setTransaction]=useState({amount:'',description:''})

  const handleDescription = (event) => {
    event.preventDefault();
    const currentDescription = event.target.value;
    setDescription(currentDescription);
  };

  const handleAmount = (event) => {
    event.preventDefault();
    const currentAmount = event.target.value;
    setAmount(currentAmount);
  };

  const handleAdd = () => {
    if (description && amount !== "") {
      setIsShowTransaction(true);
    }
  };

  return (
    <div className="flex items-center flex-col">
      <span className="font-semibold text-xl text-primary">
        Expense Tracker
      </span>
      <div className=" flex flex-col items-center bg-secondary h-[50vh] w-[70vw] rounded-md shadow-lg mt-4 pt-4 px-4 gap-10">
        <div className="flex flex-col items-center gap-3">
          <input
            type="number"
            className="border-none outline-none w-full h-[4vh] bg-background shadow-md text-text placeholder:text-text px-2 rounded-xl"
            placeholder="Amount"
            value={amount}
            onChange={handleAmount}
          />
          <input
            type="text"
            className="border-none outline-none w-full h-[4vh] bg-background shadow-md text-text placeholder:text-text px-2 rounded-xl"
            placeholder="Description"
            id="description"
            value={description}
            onChange={handleDescription}
          />
          <button
            type="button"
            className="bg-background px-5 py-2 rounded-[25px] shadow-md text-sm font-bold w-[70%] text-primary"
            style={{ WebkitTapHighlightColor: "transparent" }}
            onClick={handleAdd}
          >
            Add Transaction
          </button>
        </div>

        <div className="flex flex-col items-center">
          <p className=" underline underline-offset-4 pb-4">Transaction history</p>
          {isShowTransaction && (
            <TransactionPill description={description} amount={amount} />
          )}
        </div>
      </div>
    </div>
  );
}
