      <span className="font-semibold text-xl text-primary">
        Expense Tracker
      </span>
      <div className=" flex flex-col items-center bg-secondary h-[50vh] w-[70vw] rounded-md shadow-lg mt-4 pt-4 px-4 gap-4">
        <input
          type="number"
          className="border-none outline-none w-[85%] h-[4vh] bg-input shadow-md text-text placeholder:text-text px-2 rounded-xl placeholder:font-black"
