import classNames from 'classnames';
import React, { ReactElement } from 'react';

export function Popup({ isDarkBackground, children }) {
  const popupContentWrapperStyle = classNames(
    'pc-w-[88%] pc-h-fit pc-flex pc-items-center pc-justify-center pc-py-10 pc-px-5 pc-bg-white pc-absolute pc-top-1/2 pc-left-1/2 -pc-translate-x-1/2 -pc-translate-y-1/2 pc-rounded-larger pc-shadow-xl',
    {
      'pc-border-[5px] pc-border-white pc-border-solid pc-bg-metallic-blue':
        isDarkBackground,
    }
  );

  return (
    <div
      data-testid="popup-container"
      className="pc-backdrop-blur-xl pc-flex pc-flex-col pc-justify-end pc-absolute pc-inset-0 pc-z-50 pc-h-full pc-w-full"
    >
      <div
        data-testid="popup-content-wrapper"
        className={popupContentWrapperStyle}
      >
        {children}
      </div>
    </div>
  );
}

Popup.defaultProps = {
  isDarkBackground: false,
};
