import React from 'react';

const Popup = ({ isOpen, onClose, children }) => {
  return (
    <div className={`absolute left items-center justify-center z-50 bg-background w-full`}>
    <div className={`justify-center z-50 bg-background w-12`}>
      <div className="bg-white rounded-lg overflow-hidden shadow-xl z-10 w-96">
        <div className="bg-blue-500 px-6 py-4 text-white">
          <h2 className="text-lg font-semibold">Popup Title</h2>
        </div>
        <div className="p-6">{children}</div>
        <div className="bg-gray-100 px-6 py-4 flex justify-end">
          <button
            onClick={onClose}
            className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 focus:outline-none focus:ring focus:ring-blue-200"
          >
            Close
          </button>
        </div>
      </div>
    </div>
    </div>
  );
};

export default Popup;
